import * as api from '../api';
import { setDoctors, setLocker } from '../store/index'; // Import from the new store logic

export const getDoctors = (name) => async (dispatch) => {
    try {
        const { data } = await api.searchDoctors(name);
        dispatch(setDoctors(data)); // Use the Toolkit action
    } catch (error) {
        console.log("Search Error:", error);
    }
};

export const getLocker = (name) => async (dispatch) => {
    try {
        const { data } = await api.fetchLocker(name);
        dispatch(setLocker(data)); // Use the Toolkit action
    } catch (error) {
        console.log("Locker Error:", error);
    }
};
