import React, { useState } from 'react';
import PhysicianSection from './physicianSection';

export default function NewAppointmentForm(props) {
    const [currentStep, setCurrentStep] = useState(1);
    const [fields, setFields] = useState({
        physician: props.physician || "",
        title: "",
        startTime: ""
    });

    // THE FINAL FIX: Catch selection and change step
    const handleClick = (e) => {
        const { name, value } = e.target;
        
        setFields(prev => ({ ...prev, [name]: value }));

        // Move to step 2 if physician is selected
        if (name === "physician" && value) {
            setCurrentStep(2);
        }
    };

    return (
        <div className="p-3">
            {currentStep === 1 && (
                <PhysicianSection
                    session={props.session}
                    currentStep={currentStep}
                    name="physician"
                    handleClick={handleClick}
                />
            )}

            {currentStep === 2 && (
                <div className="text-center">
                    <h5>Physician Selected!</h5>
                    <p className="alert alert-success">Doctor: <strong>{fields.physician}</strong></p>
                    <button className="btn btn-secondary" onClick={() => setCurrentStep(1)}>Back</button>
                    {/* Your InformationSection goes here */}
                </div>
            )}
        </div>
    );
}
