import React, { useCallback, useEffect, useState } from 'react';
import { useSelector } from 'react-redux'; // 1. Added useSelector
import { FormGroup, FormLabel } from '../../../../components/form';
import { List, ListButton } from '../../../../components/lists';
import { Username } from '../../../../components/users';
import { PhysicianItem } from './utils';

export default function PhysicianSection(props) {
    const { id, name, handleClick, currentStep, ...otherProps } = props;

    // 2. Grab session directly from the Redux store
    const session = useSelector(state => state.session);

    const [state, setState] = useState({
        query: "",
        physicians: [],
        limit: 25
    });

    const getPhysicians = useCallback(async ({ search = '', page = 0, limit = 10 }) => {
        try {
            // 3. Ensure we have a token before even trying the fetch
            const token = session.authToken || session.token;
            if (!token) {
                console.warn("No token found in session!");
                return [];
            }

            const url = `/api/users`;
            const searchParams = new URLSearchParams({ view: 'physician', search, page, limit });
            
            const response = await fetch(`${url}?${searchParams.toString()}`, {
                headers: { 
                    'Authorization': `Bearer ${token}`, // Pass the Bearer token
                    'Content-Type': 'application/json'
                }
            });

            const data = await response.json();
            // Some APIs return { physicians: [] }, others return the array directly
            return response.ok ? (data.physicians || data) : [];
        } catch (err) {
            console.error("Fetch error:", err);
            return [];
        }
    }, [session]); // Re-run if session changes

    useEffect(() => {
        // Fetch data whenever query changes
        getPhysicians({ search: state.query, page: 0, limit: state.limit })
            .then(data => {
                if (Array.isArray(data)) {
                    setState(s => ({ ...s, physicians: data }));
                }
            });
    }, [state.query, state.limit, getPhysicians]);

    if (currentStep !== 1) return null;

    return (
        <>
            <FormGroup>
                <FormLabel className="text-muted">Select a Physician</FormLabel>
                <input 
                    id={id} 
                    type="text" 
                    className="form-control"
                    name="query" 
                    value={state.query} 
                    // 4. Removed physicians: [] to prevent the list from flickering to empty
                    onChange={(e) => setState({...state, query: e.target.value})}
                    placeholder="Search by name or username..." 
                    {...otherProps}
                />
            </FormGroup>
            
            {/* 5. Render list if we have data */}
            {state.physicians.length > 0 && (
                <FormGroup>
                    <List className="md-list mt-2">
                        {state.physicians.map((physician, index) => (
                            <ListButton
                                key={physician.id || index}
                                name={name} 
                                value={Username({ user: physician })} 
                                onClick={handleClick}
                            >
                                <PhysicianItem
                                    session={session} // Pass the valid session
                                    user={physician} 
                                    clickable={false} 
                                />
                            </ListButton>
                        ))}
                    </List>
                </FormGroup>
            )}
        </>
    );
}
