import React from 'react';
import { useSelector } from 'react-redux';
import { useExtendClass } from "./hooks.js";

/* --- Base Components --- */
export function List(props) {
    return (
        <ul className={useExtendClass("list-group", props.className)}>
            {props.children}
        </ul>
    );
}

export function ListItem(props) {
    return (
        <li className={useExtendClass("list-group-item", props.className)}>
            {props.children}
        </li>
    );
}

export function ListButton(props) {
    return (
        <button 
            type="button" 
            className={useExtendClass("list-group-item list-group-item-action", props.className)}
            onClick={props.onClick}
        >
            {props.children}
        </button>
    );
}

export function ListLink(props) {
    return (
        <a 
            href={props.href || "#"} 
            className={useExtendClass("list-group-item list-group-item-action", props.className)}
            onClick={props.onClick}
        >
            {props.children}
        </a>
    );
}

/* --- Redux Toolkit Components --- */

export function SearchResultsList({ onSelectDoctor }) {
    const doctors = useSelector((state) => state.patient.doctors || []);

    return (
        <div className="mt-3">
            <h6>Found Doctors:</h6>
            <List>
                {doctors.length > 0 ? doctors.map((doc) => (
                    <ListButton 
                        key={doc._id} 
                        onClick={() => {
                            console.log("ðŸŽ¯ Doctor clicked:", doc.firstName);
                            if (onSelectDoctor) onSelectDoctor(doc);
                        }}
                        className="d-flex justify-content-between align-items-center"
                    >
                        <span>
                            <strong>Dr. {doc.firstName} {doc.lastName}</strong> 
                            <br/>
                            <small className="text-muted">{doc.specialization || 'General Physician'}</small>
                        </span>
                        <span className="badge badge-primary">Select Physician</span>
                    </ListButton>
                )) : (
                    <ListItem className="text-muted text-center">
                        No doctors found. Try searching again.
                    </ListItem>
                )}
            </List>
        </div>
    );
}

export function MedicalLocker() {
    const records = useSelector((state) => state.patient.locker || []);

    return (
        <div className="mt-3">
            <h6>Medical Locker (EHR History):</h6>
            <List>
                {records.length > 0 ? records.map((record, index) => (
                    <ListItem key={index}>
                        <div className="d-flex justify-content-between">
                            <strong>{record.title}</strong>
                            <span className={`badge ${record.status === 'Completed' ? 'badge-success' : 'badge-info'}`}>
                                {record.status}
                            </span>
                        </div>
                        <div className="text-muted" style={{fontSize: '0.8rem'}}>
                            Date: {record.startTime ? new Date(record.startTime).toLocaleDateString() : 'N/A'}
                        </div>
                    </ListItem>
                )) : (
                    <ListItem className="text-muted text-center">
                        No records found in locker.
                    </ListItem>
                )}
            </List>
        </div>
    );
}
