import React, { useState } from 'react';
import PhysicianSection from './physicianSection';
import InfoSection from './infoSection';

export default function NewAppointmentForm(props) {
    const [currentStep, setCurrentStep] = useState(1);
    const [errorMessage, setErrorMessage] = useState("");
    const [fields, setFields] = useState({
        physician: props.physician || "",
        title: "",
        description: "",
        startDate: "", 
        startTime: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFields(prev => ({ ...prev, [name]: value }));
    };

    const handleClick = (e) => {
        const name = e.currentTarget.name; 
        const value = e.currentTarget.value;
        if (name && value) {
            setFields(prev => ({ ...prev, [name]: value }));
            if (name === "physician") setCurrentStep(2);
        }
    };

    const handleFinalize = async (e) => {
        if (e) e.preventDefault();
        setErrorMessage("");

        try {
            const response = await fetch('/api/appointments/new', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(fields)
            });

            const data = await response.json();

            if (response.ok) {
                alert("Appointment saved in Database! This slot will now show as RED for others.");
                // Redirect or reset
                window.location.reload(); 
            } else {
                setErrorMessage(data.error || "Booking failed.");
            }
        } catch (err) {
            setErrorMessage("Connection error to server.");
        }
    };

    return (
        <div className="md-new-appt-form p-3">
            {currentStep === 1 && (
                <PhysicianSection
                    session={props.session}
                    currentStep={currentStep}
                    name="physician"
                    handleClick={handleClick} 
                />
            )}

            {currentStep === 2 && (
                <div className="animation-fade-in">
                    <div className="alert alert-info d-flex justify-content-between align-items-center py-2 px-3 mb-4">
                        <span>Doctor: <strong>Dr. {fields.physician}</strong></span>
                        <button className="btn btn-sm btn-link" onClick={() => setCurrentStep(1)}>Change</button>
                    </div>
                    
                    <form onSubmit={handleFinalize} noValidate>
                        <InfoSection 
                            currentStep={currentStep}
                            physician={fields.physician}
                            title={fields.title}
                            description={fields.description}
                            startDate={fields.startDate}
                            startTime={fields.startTime}
                            handleChange={handleChange}
                            errorMessage={errorMessage}
                        />
                    </form>

                    <div className="mt-4">
                        <button type="button" className="btn btn-outline-secondary" onClick={() => setCurrentStep(1)}>Back</button>
                    </div>
                </div>
            )}
        </div>
    );
}
