import React, { useCallback, useEffect, useState } from 'react';
import { FormGroup, FormLabel } from '../../../../components/form';
import { List, ListButton } from '../../../../components/lists';
import { AutoLoader } from '../../../../components/loaders';
import { Username } from '../../../../components/users';
import { PhysicianItem } from './utils';

export default function PhysicianSection(props) {
    const { id, name, handleClick, ...otherProps } = props;
    const [state, setState] = useState({ query: "", physicians: [], limit: 25 });

    const getPhysicians = useCallback(async ({ search = '', page = 0, limit = 10 }) => {
        const url = `/api/users`;
        const searchParams = new URLSearchParams({ view: 'physician', search, page, limit });
        const response = await fetch(`${url}?${searchParams.toString()}`, {
            headers: { 'Authorization': `Bearer ${props.session.authToken}` }
        });
        return await response.json();
    }, [props.session.authToken]);

    useEffect(() => {
        getPhysicians({ search: state.query, page: 0, limit: state.limit })
            .then(data => setState(s => ({ ...s, physicians: data })))
            .catch(err => console.error(err));
    }, [state.query, state.limit, getPhysicians]);

    if (props.currentStep !== 1) return null;

    return (
        <>
            <FormGroup>
                <FormLabel className="text-muted">Select a Physician</FormLabel>
                <input 
                    id={id} type="text" className="form-control" name="query" 
                    value={state.query} onChange={(e) => setState({...state, query: e.target.value, physicians: []})}
                    placeholder="Search by name..." {...otherProps} 
                />
            </FormGroup>
            {state.query.length > 0 && (
                <FormGroup>
                    <List className="md-list">
                        {state.physicians.map((physician, index) => (
                            <ListButton
                                key={index}
                                name={name} // This will be "physician"
                                value={Username({ user: physician })} // The username string
                                onClick={handleClick} // FIXED: Triggers the parent handler
                            >
                                <PhysicianItem session={props.session} user={physician} clickable={false} />
                            </ListButton>
                        ))}
                    </List>
                </FormGroup>
            )}
        </>
    );
}
