import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { calculateAge } from './dates';
import { useExtendClass } from "./hooks";
import { Col, FluidContainer, Row } from './layout';
import { Loader } from './loaders';

import { getDoctors, getLocker } from '../Actions/patient'; 
import { SearchResultsList, MedicalLocker } from './lists';
import { HealthTrendsChart } from './widgets'; 

/* --- PROFILE PHOTO COMPONENTS --- */
export function Photo(props) {  
    const className = useExtendClass("img-fluid md-pfl-pic", props.className);
    if (props.isLoading) return <Loader isLoading={true} />;

    const imageSource = (props.src instanceof Blob) ? URL.createObjectURL(props.src) : props.src;

    return (
        <img id={props.id} alt={props.alt} className={className} 
             src={imageSource || "/default-avatar.png"} 
             onError={(e) => { e.target.src = "/default-avatar.png"; }} />
    );
}

export function ProfilePhoto(props) {
    const [isLoading, setIsLoading] = useState(true);
    const [source, setSource] = useState("");
    const session = useSelector(state => state.session);

    useEffect(() => {
        async function loadPhoto() {
            if (!props.user?.profilePhotoId) { setIsLoading(false); return; }
            try {
                const token = session?.authToken || session?.token;
                const response = await fetch(`/api/users/${props.user.username}/photos/${props.user.profilePhotoId}`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (response.ok) {
                    const blob = await response.blob();
                    setSource(blob);
                }
            } catch (err) { console.error(err); } finally { setIsLoading(false); }
        }
        loadPhoto();
    }, [props.user, session]);

    return <Photo src={source} isLoading={isLoading} />;
}

/* --- THE UPDATED DASHBOARD --- */
export function MemberADashboard() {
    const [searchTerm, setSearchTerm] = useState('');
    const [hasSearched, setHasSearched] = useState(false); // Only show list after search
    const dispatch = useDispatch();
    const session = useSelector(state => state.session);
    const records = useSelector((state) => state.patient.locker || []);

    useEffect(() => {
        if (session.username) { dispatch(getLocker(session.username)); }
    }, [dispatch, session.username]);

    const handleSearchClick = () => {
        if (searchTerm.trim()) {
            setHasSearched(true);
            dispatch(getDoctors(searchTerm));
        }
    };

    return (
        <FluidContainer className="mt-5">
            <Row>
                <Col className="col-md-10 offset-md-1">
                    <div className="card shadow-sm p-4">
                        <h2 className="text-primary mb-4 border-bottom pb-2">Patient Dashboard</h2>
                        
                        <div className="mb-5">
                            <h5 className="mb-3">Smart Doctor Search</h5>
                            <div className="input-group mb-3">
                                <input 
                                    type="text" 
                                    className="form-control form-control-lg" 
                                    placeholder="Enter specialist name..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                                <div className="input-group-append">
                                    <button className="btn btn-primary px-4" onClick={handleSearchClick}>Find Doctor</button>
                                </div>
                            </div>
                            {/* RESULTS ONLY APPEAR AFTER CLICKING SEARCH */}
                            {hasSearched && <SearchResultsList />}
                        </div>

                        <div className="mb-5">
                            <h5 className="mb-3">My Medical Locker</h5>
                            <MedicalLocker />
                        </div>

                        <div>
                            <h5 className="mb-3">Predictive Health Insights</h5>
                            {records.length > 0 ? <HealthTrendsChart lockerData={records} /> : <div className="alert alert-info">No records.</div>}
                        </div>
                    </div>
                </Col>
            </Row>
        </FluidContainer>
    );
}

// RESTORED FOR BANNER.JS
export function FullName(props) { return (props.user) ? (props.user.isPhysician ? `Dr. ${props.user.firstName} ${props.user.lastName}` : `${props.user.firstName} ${props.user.lastName}`) : ""; }
export function Email(props) { return (props.user && props.user.emailId) ? props.user.emailId : ""; }
export function PhoneNumber(props) { return (props.user && props.user.phoneNumber) ? props.user.phoneNumber : ""; }
export function BioData(props) {
    if (!props.user) return "";
    const bio = props.user.isPhysician ? [props.user.qualification, props.user.specialization] : [calculateAge(props.user.dob), props.user.gender];
    return bio.filter(Boolean).join(", ");
}
