import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:3003' });

export const searchDoctors = (name, token) => {
    // If token is missing, don't send the header at all to avoid "malformed" errors
    const config = token ? { headers: { Authorization: `Bearer ${token}` } } : {};
    return API.get(`/api/users?view=physician&name=${name}`, config);
};

export const fetchLocker = (username, token) => {
    const config = token ? { headers: { Authorization: `Bearer ${token}` } } : {};
    return API.get(`/api/users/${username}/locker`, config);
};
