import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { calculateAge } from './dates';
import { useExtendClass } from "./hooks";
import { Col, FluidContainer, Row } from './layout';
import { Loader } from './loaders';

/* --- NEW IMPORTS FOR MEMBER A --- */
import { getDoctors, getLocker } from '../Actions/patient'; 
import { SearchResultsList, MedicalLocker } from './lists';
import { HealthTrendsChart } from './widgets'; 

/* Generic User Components */
export function FullName(props) {
    if (props.user) {
        if (props.user.isPhysician) {
            return `Dr. ${props.user.firstName} ${props.user.lastName}`;
        } else {
            return `${props.user.firstName} ${props.user.lastName}`;
        }
    }
    return "";
}

export function Username(props) {
    return (props.user && props.user.username) ? props.user.username : "";
}

export function BioData(props) {
    if (props.user) {
        const bioArray = [];
        if (props.user.isPhysician) {
            if (props.user.qualification) bioArray.push(props.user.qualification);
            if (props.user.specialization) bioArray.push(props.user.specialization);
        } else {
            if (props.user.dob) bioArray.push(calculateAge(props.user.dob));
            if (props.user.gender) bioArray.push(props.user.gender);
        }
        return bioArray.join(", ");
    }
    return "";
}

export function Currency(props) {
    if (props.value) {
        return Intl.NumberFormat('en-US', {style: 'currency', currency: 'USD'}).format(props.value);
    }
    return "";
}

/* Gender, Qualification, Specialization Arrays (unchanged) */
export const Genders = ["Male", "Female", "Other"];
export const Qualifications = ["MBBS", "MD", "MS", "Intern"];
export const Specializations = [
    "Cardiologist", "Radiologist", "Psychiatrist", "Anesthesiologist", 
    "Pediatrician", "Neurologist", "Dermatologist", "Dentist", 
    "Pathologist", "General Surgeon", "Orthopaedic Surgeon", 
    "Urologist", "Nephrologist", "Oncologist", "Gynaecologist"
];

/* Input Components (unchanged logic) */
export function GenderInput(props) {
    return (
        <select id={props.id} name={props.name} className={useExtendClass("custom-select", props.className)}
                value={props.value} onChange={props.handleChange} required={!!props.required}>
            {Genders.map((g, i) => <option key={i} value={g}>{g}</option>)}
        </select>
    );
}

export function QualificationInput(props) {
    return (
        <select id={props.id} name={props.name} className={useExtendClass("custom-select", props.className)}
                value={props.value} onChange={props.handleChange} required={!!props.required}>
            {Qualifications.sort().map((q, i) => <option key={i} value={q}>{q}</option>)}
        </select>
    );
}

export function SpecializationInput(props) {
    return (
        <select id={props.id} name={props.name} className={useExtendClass("custom-select", props.className)}
                value={props.value} onChange={props.handleChange} required={!!props.required}>
            {Specializations.sort().map((s, i) => <option key={i} value={s}>{s}</option>)}
        </select>
    );
}

/* Photo Components - FIXED IMAGE RENDERING */
export function Photo(props) {  
    const className = useExtendClass("img-fluid md-pfl-pic", props.className);

    if (props.isLoading) {
        return (    
            <div className={className} style={{display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f8f9fa'}}>
                <Loader isLoading={true} />
            </div>
        );
    } 

    // FIXED: If src is a Blob/File, create a URL. If it's already a string/URL, use it directly.
    const imageSource = (props.src instanceof Blob || props.src instanceof File) 
        ? URL.createObjectURL(props.src) 
        : props.src;

    return (
        <img id={props.id} alt={props.alt || "User Photo"} className={className} 
             src={imageSource || "/default-avatar.png"} 
             onError={(e) => { e.target.src = "/default-avatar.png"; }} />
    );
}

export function ProfilePhoto(props) {
    const [isLoading, setIsLoading] = useState(true);
    const [source, setSource] = useState("");
    // FIXED: Get session from Redux if not in props
    const session = useSelector(state => state.session);

    useEffect(() => {
        async function loadPhoto() {
            if (!props.user || !props.user.profilePhotoId) {
                setIsLoading(false);
                return;
            }

            try {
                setIsLoading(true);
                const token = session.authToken || session.token;
                const response = await fetch(`/api/users/${props.user.username}/photos/${props.user.profilePhotoId}`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });

                if (response.ok) {
                    const blob = await response.blob();
                    setSource(blob);
                }
            } catch (err) {
                console.error(`Photo load error: ${err}`);
            } finally {
                setIsLoading(false);
            }
        }
        loadPhoto();
    }, [props.user, session]);

    return <Photo id={props.id} className={props.className} src={source} isLoading={isLoading} />;
}

/* --- MEMBER A: PATIENT DASHBOARD COMPONENT --- */
export function MemberADashboard() {
    const [searchTerm, setSearchTerm] = useState('');
    const dispatch = useDispatch();
    const session = useSelector(state => state.session);
    const records = useSelector((state) => state.patient.locker || []);

    useEffect(() => {
        // Use the logged-in username dynamically
        if (session.username) {
            dispatch(getLocker(session.username));
        }
    }, [dispatch, session.username]);

    const handleSearchClick = () => {
        dispatch(getDoctors(searchTerm));
    };

    return (
        <FluidContainer className="mt-5">
            <Row>
                <Col className="col-md-10 offset-md-1">
                    <div className="card shadow-sm p-4">
                        <h2 className="text-primary mb-4 border-bottom pb-2">Patient Dashboard</h2>
                        
                        <div className="mb-5">
                            <h5 className="mb-3 font-weight-bold">Smart Doctor Search</h5>
                            <div className="input-group mb-3">
                                <input 
                                    type="text" 
                                    className="form-control form-control-lg" 
                                    placeholder="Search specialist (e.g. Pradnya)..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                                <div className="input-group-append">
                                    <button className="btn btn-primary px-4" onClick={handleSearchClick}>
                                        Find Doctor
                                    </button>
                                </div>
                            </div>
                            <SearchResultsList />
                        </div>

                        <div className="mb-5">
                            <h5 className="mb-3 font-weight-bold">My Secure Medical Locker</h5>
                            <MedicalLocker />
                        </div>

                        <div>
                            <h5 className="mb-3 font-weight-bold">Predictive Health Insights</h5>
                            {records.length > 0 ? (
                                <HealthTrendsChart lockerData={records} />
                            ) : (
                                <div className="alert alert-info">No medical records found to generate health trends.</div>
                            )}
                        </div>
                    </div>
                </Col>
            </Row>
        </FluidContainer>
    );
}
