import React, { useState } from 'react';
import PhysicianSection from './physicianSection';

export default function NewAppointmentForm(props) {
    const [currentStep, setCurrentStep] = useState(1);
    const [fields, setFields] = useState({
        physician: props.physician || "",
        title: "",
        description: "",
        startTime: "",
        endTime: ""
    });

    // THE CORE FIX: Catch selection, update state, and move to step 2
    const handleClick = (e) => {
        // e.target refers to the ListButton
        const name = e.target.name; 
        const value = e.target.value;

        console.log("ðŸŽ¯ Selected:", name, value);

        if (name && value) {
            setFields(prev => ({
                ...prev,
                [name]: value
            }));

            // If a doctor was chosen, go to the next step
            if (name === "physician") {
                setCurrentStep(2);
            }
        }
    };

    const nextStep = () => setCurrentStep(prev => prev + 1);
    const prevStep = () => setCurrentStep(prev => prev - 1);

    return (
        <div className="md-new-appt-form p-3">
            {currentStep === 1 && (
                <PhysicianSection
                    session={props.session}
                    currentStep={currentStep}
                    name="physician" // Critical: matches the 'if' condition in handleClick
                    handleClick={handleClick} 
                />
            )}

            {currentStep === 2 && (
                <div className="animation-fade-in text-center">
                    <h5 className="mb-3">Step 2: Enter Appointment Info</h5>
                    <div className="alert alert-info py-2">
                        Physician: <strong>{fields.physician}</strong>
                    </div>
                    
                    {/* Placeholder for your actual form inputs */}
                    <p className="text-muted">You can now enter the Title and Date.</p>

                    <div className="mt-4 d-flex justify-content-between">
                        <button className="btn btn-outline-secondary" onClick={prevStep}>Back</button>
                        <button className="btn btn-primary" onClick={nextStep}>Finalize</button>
                    </div>
                </div>
            )}
        </div>
    );
}
