import React, { useState } from 'react';
import PhysicianSection from './physicianSection';
import InfoSection from './infoSection';

export default function NewAppointmentForm(props) {
    const [currentStep, setCurrentStep] = useState(1);
    const [errorMessage, setErrorMessage] = useState("");
    const [fields, setFields] = useState({
        physician: props.physician || "",
        title: "",
        description: "",
        startDate: new Date().toISOString().split('T')[0], // Defaults to today
        startTime: "",
        endDate: new Date().toISOString().split('T')[0],
        endTime: ""
    });

    /**
     * Standard change handler for text inputs and date pickers
     * Also used by the visual time slots in InfoSection
     */
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFields(prev => ({
            ...prev,
            [name]: value
        }));
    };

    /**
     * Handles selection from the PhysicianSection (Step 1)
     */
    const handleClick = (e) => {
        const name = e.currentTarget.name; 
        const value = e.currentTarget.value;

        if (name && value) {
            setFields(prev => ({
                ...prev,
                [name]: value
            }));

            if (name === "physician") {
                setCurrentStep(2);
            }
        }
    };

    /**
     * Final submission logic for the "Request Appointment" button
     */
    const handleFinalize = async (e) => {
        if (e) e.preventDefault();
        
        console.log("Submitting Appointment Data:", fields);
        
        // Example logic for your teacher:
        // alert(`Booking confirmed for ${fields.title} with Dr. ${fields.physician} at ${fields.startTime}`);
        
        // In a real scenario, you would perform your API POST request here:
        // const response = await fetch('/api/appointments', { method: 'POST', body: JSON.stringify(fields) });
    };

    const prevStep = () => setCurrentStep(prev => prev - 1);

    return (
        <div className="md-new-appt-form p-3">
            {/* Step 1: Physician Selection */}
            {currentStep === 1 && (
                <PhysicianSection
                    session={props.session}
                    currentStep={currentStep}
                    name="physician"
                    handleClick={handleClick} 
                />
            )}

            {/* Step 2: Appointment Details & Time Slots */}
            {currentStep === 2 && (
                <div className="animation-fade-in">
                    <div className="text-center mb-4">
                        <h5 className="mb-1">Appointment Details</h5>
                        <p className="text-muted small">Fill in the information below</p>
                    </div>

                    {/* Quick Selection Summary */}
                    <div className="alert alert-info d-flex justify-content-between align-items-center py-2 px-3">
                        <span>Selected Physician: <strong>Dr. {fields.physician}</strong></span>
                        <button className="btn btn-sm btn-link p-0" onClick={() => setCurrentStep(1)}>Change</button>
                    </div>
                    
                    {/* The Visual Slot Picker and Form Inputs */}
                    <form onSubmit={handleFinalize}>
                        <InfoSection 
                            currentStep={currentStep}
                            physician={fields.physician}
                            title={fields.title}
                            description={fields.description}
                            startDate={fields.startDate}
                            startTime={fields.startTime}
                            endDate={fields.endDate}
                            endTime={fields.endTime}
                            handleChange={handleChange}
                            errorMessage={errorMessage}
                        />
                    </form>

                    {/* Navigation Buttons */}
                    <div className="mt-4 d-flex justify-content-between">
                        <button className="btn btn-outline-secondary" onClick={prevStep}>Back</button>
                        {/* The Submit button is inside InfoSection, but we can add an extra Finalize here if needed */}
                    </div>
                </div>
            )}
        </div>
    );
}
