import React, { useState } from 'react';
import PhysicianSection from './physicianSection';

export default function NewAppointmentForm(props) {
    const [currentStep, setCurrentStep] = useState(1);
    const [fields, setFields] = useState({
        physician: props.physician || "",
        title: "",
        description: "",
        startTime: "",
        endTime: ""
    });

    /**
     * Handles selection from the PhysicianSection
     * Uses e.currentTarget to ensure we get data from the Button, 
     * not the text/strong tag inside it.
     */
    const handleClick = (e) => {
        const name = e.currentTarget.name; 
        const value = e.currentTarget.value;

        console.log("ðŸŽ¯ Selection Triggered:", name, value);

        if (name && value) {
            setFields(prev => ({
                ...prev,
                [name]: value
            }));

            // Check if we just selected the physician to move to step 2
            if (name === "physician") {
                console.log("ðŸš€ Switching to Step 2");
                setCurrentStep(2);
            }
        }
    };

    const nextStep = () => setCurrentStep(prev => prev + 1);
    const prevStep = () => setCurrentStep(prev => prev - 1);

    return (
        <div className="md-new-appt-form p-3">
            {/* Step 1: Physician Selection */}
            {currentStep === 1 && (
                <PhysicianSection
                    session={props.session}
                    currentStep={currentStep}
                    name="physician"
                    handleClick={handleClick} 
                />
            )}

            {/* Step 2: Appointment Details */}
            {currentStep === 2 && (
                <div className="animation-fade-in">
                    <div className="text-center mb-4">
                        <h5 className="mb-1">Appointment Details</h5>
                        <p className="text-muted small">Fill in the information below</p>
                    </div>

                    <div className="alert alert-info d-flex justify-content-between align-items-center">
                        <span>Selected Physician: <strong>Dr. {fields.physician}</strong></span>
                        <button className="btn btn-sm btn-link" onClick={() => setCurrentStep(1)}>Change</button>
                    </div>
                    
                    {/* Your InfoSection or inputs for Title/Description/Time would go here */}
                    <div className="p-4 border rounded bg-light text-center">
                        <p>Detailed Information Form Loading...</p>
                    </div>

                    <div className="mt-4 d-flex justify-content-between">
                        <button className="btn btn-outline-secondary" onClick={prevStep}>Back</button>
                        <button className="btn btn-primary" onClick={nextStep}>Finalize</button>
                    </div>
                </div>
            )}
        </div>
    );
}
