import React from 'react';
import { useSelector } from 'react-redux';
import { useExtendClass } from "./hooks.js";

/* --- KEEP EXISTING REPO CODE START --- */
export function List(props) {
    return (
        <ul className={useExtendClass("list-group", props.className)}>
            {props.children}
        </ul>
    );
}

export function ListItem(props) {
    return (
        <li className={useExtendClass("list-group-item", props.className)}>
            {props.children}
        </li>
    );
}

// Added ListButton to fix "Attempted import error"
export function ListButton(props) {
    return (
        <button 
            type="button" 
            className={useExtendClass("list-group-item list-group-item-action", props.className)}
            onClick={props.onClick}
        >
            {props.children}
        </button>
    );
}
/* --- KEEP EXISTING REPO CODE END --- */


/* --- MEMBER A: UPDATED FOR REDUX TOOLKIT --- */

// Component to show Search Results
export function SearchResultsList() {
    // UPDATED: Now points to state.patient.doctors because of Redux Toolkit Slice
    const doctors = useSelector((state) => state.patient.doctors || []);

    return (
        <div className="mt-3">
            <h6>Found Doctors:</h6>
            <List>
                {doctors.length > 0 ? doctors.map((doc) => (
                    <ListItem key={doc._id} className="d-flex justify-content-between align-items-center">
                        <span>
                            <strong>Dr. {doc.firstName} {doc.lastName}</strong> 
                            <br/>
                            <small className="text-muted">{doc.specialization || 'General Physician'}</small>
                        </span>
                        <span className="badge badge-primary">Available</span>
                    </ListItem>
                )) : (
                    <ListItem className="text-muted text-center">
                        No doctors found. Try searching "Pradnya".
                    </ListItem>
                )}
            </List>
        </div>
    );
}

// Component to show Patient Medical History (Locker)
export function MedicalLocker() {
    // UPDATED: Now points to state.patient.locker because of Redux Toolkit Slice
    const records = useSelector((state) => state.patient.locker || []);

    return (
        <div className="mt-3">
            <h6>Medical Locker (EHR History):</h6>
            <List>
                {records.length > 0 ? records.map((record, index) => (
                    <ListItem key={index}>
                        <div className="d-flex justify-content-between">
                            <strong>{record.title}</strong>
                            <span className={`badge ${record.status === 'Completed' ? 'badge-success' : 'badge-info'}`}>
                                {record.status}
                            </span>
                        </div>
                        <div className="text-muted" style={{fontSize: '0.8rem'}}>
                            Date: {record.startTime ? new Date(record.startTime).toLocaleDateString() : 'N/A'}
                        </div>
                        {record.description && (
                            <p className="mb-0 mt-1" style={{fontSize: '0.85rem'}}>{record.description}</p>
                        )}
                    </ListItem>
                )) : (
                    <ListItem className="text-muted text-center">
                        No records found in locker.
                    </ListItem>
                )}
            </List>
        </div>
    );
}
